S=[.1;.2;.5;1;2;3.5;5];V=[.04;.08;.16;.26;.32;.39;.42];p=polyfit(-V./S,V,1); % find a linear fit to V as a function of -V/S s=[0:100]/20;g=p(2)*s./(s+p(1)); %plot the Michaelis-Menten functionfigure(1);plot(S,V,'*',s,g,'r','linewidth',2)xlabel('Substrate Concentration S','fontsize',14)ylabel('Reaction Velocity V','fontsize',14)t=-.004*[0:100];f = polyval(p,t);figure(2)plot(t,f,'g',-V./S,V,'*','linewidth',2) % plot the linear fitxlabel('V/S','fontsize',14)ylabel('V','fontsize',14)