clear allclose allclcset(0,                           ...   'defaultaxesfontsize', 20,   ...   'defaultaxeslinewidth', 1.0, ...   'defaultlinelinewidth', 1.2, ...   'defaultpatchlinewidth', 0.7);   S=[.2;.5;1;1.5;2;2.5;;3.5;4;4.5;5];enop=2.5;KM=1.5;VD=S.^enop./(KM^enop+S.^enop);vmax = 1;V=[.01;.060;.27;.50;.67;.78;.89;.92;.94;.95];G=log(V./(1-V));ls=log(S);N=10;A=[ones(1,N)',ls];B=transpose(A)*A;c=transpose(A)*G;b=B\c;s=[0:100]/20;n=b(2);km = exp(-b(1));gv=s.^n./(s.^n+km);figure(1);plot(S,V,'k*',s,gv,'r','linewidth',2)xlabel('Substrate Concentration s','fontsize',16)ylabel('Velocity V','fontsize',16)f=b(2)*ls+b(1);figure(2);plot(ls,f,'g','linewidth',2)hold onplot(ls,G,'k*')xlabel('ln(s)','fontsize',16);ylabel('ln(V/(V_{max}-V)','fontsize',16)Km=exp(-b(1)/b(2))