%to make plots for ex_two_enzyme%parameter valuesl1=.9; l2=.8; m1=0.5; m2=0.6; a1=0.3;sv2 = -l1*l2+a1*m1*l2+m2*l1-a1*m2*l1;sv1 = -l2+a1*m1+a1*l2+m2-a1*m2-a1*l1;th1=-sv1;th2=-sv2;s = [.01:.01:1];I1 = - s/th1 - l1*l2*log(s)/th2+log(th1*s +th2)*l1*l2/th2 ...	- log(th1* s + th2)*l1/th1-log(th1* s + th2) *l2/th1+th2*log(th1* s+th2)/th1^2;K =  -1/th1 + log(th1 +th2)*l1*l2/th2 ...	- log(th1+ th2)*l1/th1-log(th1 + th2)*l2/th1+th2 *log(th1+th2)/th1^2;	expr = I1-K;x = s./(l1+s);y = s./(l2+s);plot(expr,s,'r',expr,x,'g',expr,y,'y','linewidth',2)xlabel('time (dimensionless)','fontsize',18)text(2,.4,'\it x','fontsize',16)text(2,.5,'\it y','fontsize',16)text(2,.78,'\sigma','fontsize',18)		