N=100;dx = 1/N;%first a typical profiles(1) = .1;a=30;t(1) = 0;for j=1:Ns(j+1) = s(j)+dx*t(j);t(j+1) = t(j) + dx*a*s(j)/(1+s(j));endx=.01*[0:N];figure(1);plot(x,s,'r',x,s./(1+s),'b');legend('substrate concentration','local reaction rate')xlabel('x')ylabel('s');title('Solution Profile');%Now look at several values of external concentration:M=20;s1=.0001;s2=.001;s3=.01;s4=.1;S=[s1*[1:M],s1*M+s2*[1:M],(s1+s2)*M+s3*[1:M],(s1+s2+s3)*M+s4*[1:M]];T=zeros(1,4*M);for j=1:NS = S + dx*T;T = T +dx*a*S./(1+S);endE=T/a;Se=S./(S+1);figure(2);plot(S,E,'r',S,Se,'b','linewidth',2)legend('Difussion-limited Reactor','Well-stirred Reactor',4);title('Effective Production Rate');xlabel('External Substrate Concentration');