      implicit none	real w,w0,R,L,C,gam	complex v1,v2,v3	integer i		R = 1.0	L = 1.0	C = 1.0	w0 = 1.0		open(2,file='aafr.out')			do i = 1,2000    	    w = 0.1 + (i-1.0)*5.0/999.0	    	    gam = 1.0	    v1 = cmplx(R/(L*C),w/C) / cmplx(w0*w0 - w*w,gam*w)	    gam = 0.5	    v2 = cmplx(R/(L*C),w/C) / cmplx(w0*w0 - w*w,gam*w)	    gam = 0.1	    v3 = cmplx(R/(L*C),w/C) / cmplx(w0*w0 - w*w,gam*w)          write(2,*) w,cabs(v1),cabs(v2),cabs(v3)      end do		stop	end	